update.foreach:
update edu_history
 set attendance = #{item.attendance}
 where edu_hisno = #{item.eduHisno}

mapper.select:
select * from (select rownum rn, history_list.* from (select h.edu_hisno, d.edu_title, e.emp_no, e.emp_name, h.attendance, h.score from employee e, edu d, edu_history h where e.emp_no = h.emp_no and d.edu_no = h.edu_no
and d.edu_title like '%' || #{keyword} || '%'
and h.emp_no = #{keyword}
and e.emp_name like '%' || #{keyword} || '%'
order by h.edu_hisno DESC) history_list) WHERE rn BETWEEN #{startNum} AND #{endNum}

select count(*) from employee e, department dept, position pos where dept.dept_no = e.dept_no and pos.pos_no = e.pos_no
and dept.dept_name like '%' || #{keyword} || '%'
and pos.pos_name = #{keyword}

select *
from(select rownum rn, emp_list.* 
from(select e.emp_no, e.emp_name, pos.pos_name, dept.dept_name
from employee e, department dept, position pos
where dept.dept_no = e.dept_no
and pos.pos_no = e.pos_no

	
		and dept.dept_name like '%' || #{keyword} || '%'
	
	
		and pos.pos_name = #{keyword}
	
) emp_list) where rn between #{startNum} and #{endNum}

update.foreach:
update edu_history
set score = upper(#{vo.score})
where edu_hisno = #{vo.eduHisno}

update edu
set edu_progress = #{item.eduProgress}
where edu_no = #{item.eduNo}
